import React,{useReducer} from 'react';
import "./Style.css";

const reducer = (state,action) => {
     if(action.type === "on"){
         state = "https://o.remove.bg/downloads/e6440eff-5960-4a7f-aa53-36785df447ef/1-removebg-preview.png"
     }
     if(action.type === "off"){
        state = "https://o.remove.bg/downloads/35250298-bd4a-4188-8631-f3154d752c17/light_bulb-removebg-preview.png"
    }
     return state;
}


function Room() {
    const initialState = "https://o.remove.bg/downloads/35250298-bd4a-4188-8631-f3154d752c17/light_bulb-removebg-preview.png";



    const [state, dispatch] = useReducer( reducer, initialState)
    return (
        <>

        <div className="center_div">
            <img style={{width:"250px"}} src={state} />
            <p ></p>
        <div class="button2" onClick={() => dispatch({type:"on"})}  >
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          ON
        </div>

        <div class="button2" onClick={() => dispatch({type:"off"})}  >
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          OFF
        </div>
        </div>

        </>
    )
}

export default Room
