import React, {useState} from 'react';
import './Style.css';


function UsestateHook() {
  const [value,setvalue] = useState(0);

    return (
        <>
      <div className="center_div">
        <p>{value}</p>
        <div class="button2" onClick={() => {
         return(
           setvalue(value + 1)
         )
        }} >
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          INCR
        </div>
        <div
          class="button2" onClick={() => {
            return
              if(value > 0){
                setvalue(value - 1)
              } else {
                setvalue(0)
              }


           }} 
          >
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          DECR
        </div>
      </div>
        </>
    )
}

export default UsestateHook
